mapper.delete:

delete from friendrange
where userId = #{userid,jdbcType=INTEGER}
  and friendId = #{friendid,jdbcType=INTEGER}

mapper.update:

update friendrange
set creatTimeStamp = #{creattimestamp,jdbcType=BIGINT},
  `status` = #{status,jdbcType=INTEGER}
where userId = #{userid,jdbcType=INTEGER}
  and friendId = #{friendid,jdbcType=INTEGER}

mapper.select:

