mapper.delete:

delete from history
where userId = #{userid,jdbcType=INTEGER}
  and contentId = #{contentid,jdbcType=VARCHAR}

mapper.update:

update history
set createStamp = #{createstamp,jdbcType=BIGINT},
  isCollected = #{iscollected,jdbcType=INTEGER}
where userId = #{userid,jdbcType=INTEGER}
  and contentId = #{contentid,jdbcType=VARCHAR}

mapper.select:

select userId, contentId, createStamp, isCollected
from history
where userId = #{userid,jdbcType=INTEGER}
  and contentId = #{contentid,jdbcType=VARCHAR}
